Chart.defaults.global.defaultFontColor = "#899096";
var randomScalingFactor = function () {
        return Math.round(100 * Math.random())
    },
    renderBarChart = function () {
        var a = {
                labels: ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"],
                datasets: [{
                    borderWidth: 2,
                    borderColor: "#30373e",
                    backgroundColor: "#30373e",
                    data: [randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor()],
                    label: "Total Item Sold"
                }, {
                    borderWidth: 2,
                    borderColor: "#17B6A4",
                    backgroundColor: "#17B6A4",
                    data: [randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor(), randomScalingFactor()],
                    label: "Total Sales"
                }]
            },
            r = document.getElementById("monthly-report-chart").getContext("2d");
        new Chart(r, {
            type: "bar",
            data: a,
            options: {
                legend: {
                    display: !0
                },
                scales: {
                    scaleLabel: {
                        fontColor: "#aab3ba"
                    },
                    gridLines: {
                        color: "rgba(255,255,255,0.1)"
                    }
                }
            }
        })
    },
    renderDataTableSparkline = function () {
        $('[data-render="sparkline"]').each(function () {
            var a = function () {
                    return Math.round(100 * Math.random())
                },
                r = [a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a(), a()];
            $(this).sparkline(r, {
                type: "line",
                width: "100%",
                height: "28px",
                fillColor: "transparent",
                spotColor: "#F04B46",
                lineColor: "#8a8f94",
                minSpotColor: "#F04B46",
                maxSpotColor: "#F04B46",
                lineWidth: 1.5,
                spotRadius: 2
            })
        })
    },
    handleDataTableDefault = function () {
        "use strict";
        0 !== $("#data-table").length && ($("#data-table").DataTable({
            responsive: !0,
            pageLength: 10,
            lengthMenu: [
                [10, 20, -1],
                [10, 20, "All"]
            ],
            initComplete: function () {
                renderDataTableSparkline()
            }
        }), 
        $("#data-table").on("draw.dt", function () {
            renderDataTableSparkline()
        }))
    },
    handleDashboardGritterNotification = function () {
        setTimeout(function () {
            $.gritter.add({
                title: "Welcome back, Admin!",
                text: "You have 5 new notifications. Please check your inbox.",
                image: "assets/img/user_profile.jpg",
                sticky: !0,
                time: "",
                class_name: "my-sticky-class"
            })
        }, 1e3)
    },
    PageDemo = function () {
        "use strict";
        return {
            init: function () {
                renderBarChart(), 
                handleDataTableDefault()
                // , 
                // handleDashboardGritterNotification()
            }
        }
    }();
